# Set up gas prices and limits
GAS_PRICE = w3.toWei('5', 'gwei')
GAS_LIMIT = 1000000

# Set up PancakeSwap Factory contract
factory_contract = w3.eth.contract(
    address=PANCAKESWAP_FACTORY_ADDRESS,
    abi=[
        {
            "inputs": [
                {"internalType": "address", "name": "", "type": "address"},
                {"internalType": "address", "name": "", "type": "address"}
            ],
            "name": "getPair",
            "outputs": [{"internalType": "address", "name": "", "type": "address"}],
            "stateMutability": "view",
            "type": "function"
        }
    ]
)

# Set up PancakeSwap Router contract
router_contract = w3.eth.contract(address=PANCAKESWAP_ROUTER_ADDRESS, abi=ROUTER_ABI)

def getPair(token_address):
    pair_address = factory_contract.functions.getPair(token_address, router_contract.functions.WETH().call()).call()
    return pair_address

while True:
    liquidity = getPair(TOKEN_ADDRESS)
